function ok=cursor(ok)%%	CURSOR%%	TCH 7-21-93%%	Usage:	cu%%	Used to locate points on the current plot.  Called by the script cu.%	Puts a crosshair cursor on the graphics window, and displays the %	(x,y) data pair corresponding to the cursor position whenever the left %	mouse button is pressed.  Press the right mouse button to end.%	x=0;	y=0;	button=0;%%	Get points from the graph until the user presses the right mouse buttton%	while button ~=3		[x,y,button]=ginput(1);		if button ~=3			s=sprintf('X= %-8.6g   Y= %-10.7g',x,y);			disp(s)		end	end