function [r_amu, r_rho, abs_edge] = atomdata( zed )% Determine amu, rho, number and energy of absorption edges% Usage: [r_amu, r_rho, abs_edge] = atomdata( zed )% Fortran version written by Sean Brennan at SSRL, described in% S. Brennan and P.L. Cowan, Rev. Sci. Instrum., 63, 850 (1992).% V3.0 ported to MATLAB, Alex Lessmann (1.0)% calls to amu_rho.mat bnd_nrg.matglobal	DISK_PATH DISK_DELIM DISK_END ANOMAL_FILES ... 		CL_AMU CL_RHO CL_BIND_NRG CL_N_ORBif isempty( CL_AMU)    %load([DISK_PATH,ANOMAL_FILES,DISK_DELIM,'crom_dir',DISK_END,'amu_rho'] );    load('/Users/shrus/m_scripts/anomalous/data_dir/crom_dir/amu_rho');endif isempty( CL_BIND_NRG)    %load([DISK_PATH,ANOMAL_FILES,DISK_DELIM,'crom_dir',DISK_END,'bnd_nrg'] );    load('/Users/shrus/m_scripts/anomalous/data_dir/crom_dir/bnd_nrg');endif( ( zed < 1) | ( zed > 92) )    zed = 0;    error( 'Atomic number out of range' )else    r_amu = CL_AMU(zed);    r_rho = CL_RHO(zed);    % bind_nrg(1) = K in keV    % bind_nrg(2) = L1 "  "    % bind_nrg(5) = M1 "  "	abs_edge = CL_BIND_NRG(zed,:);endreturn