function crom_i% Initialize all the GUI controls for cromerg.m% Copyright 2002 Sean M. Brennan.% Stanford Synchrotron Radiation Laboratory% Stanford Linear Accelerator Center, Stanford CA 94309% Bren@slac.stanford.edu	figure('numbertitle','off','Name','CROMERG','units','character', ...	'position',[ 10 10 100 40],'resize','off','color',[0 0 .4 ]); %	set(gca,'visible','off','position',[0 0 1 1]); %	set(gcf, 'DefaultTextFontSize',14);%	set(gcf,'NextPlot','new')  			%  Uicontrol Object Creation 	element = uicontrol(... 		'BackgroundColor',[ 1 1 1],... 		'ForegroundColor',[ 0 0 0 ],... 		'Units','normalized',... 		'Position',[ 0.29 0.02 0.11 0.05 ],... 		'String','cu',... 		'Style','edit',... 		'Visible','on',... 		'tag','crom_elem'); 	emin = uicontrol(... 		'BackgroundColor',[ 1 1 1],... 		'ForegroundColor',[ 0 0 0 ],... 		'Units','normalized',... 		'Position',[ 0.41 0.02 0.11 0.05 ],... 		'String','8500',... 		'Style','edit',... 		'Visible','on',... 		'tag','crom_emin'); 	emax = uicontrol(... 		'BackgroundColor',[ 1 1 1],... 		'ForegroundColor',[ 0 0 0 ],... 		'Units','normalized',... 		'Position',[ 0.53 0.02 0.11 0.05 ],... 		'String','9500',... 		'Style','edit',... 		'Visible','on',... 		'tag','crom_emax');	plot = uicontrol(... 		'BackgroundColor',[ 0.7 0.7 0.7 ],... 		'CallBack','cromerg(''plot'');',... 		'ForegroundColor',[ 0 0 0 ],... 		'Units','normalized',... 		'Position',[ 0.65 0.02 0.11 0.05 ],... 		'String','Plot',... 		'Style','pushbutton',... 		'Visible','on',... 		'tag','crom_plot'); 	bgcolor=get(gcf,'color');	if sum(bgcolor)>1, txtcolor=[0 0 0]; else txtcolor=[1 1 1]; end	t_elem = uicontrol(... 		'BackgroundColor',bgcolor,... 		'ForegroundColor',txtcolor,... 		'Units','normalized',... 		'Position',[ 0.29 0.0 0.11 0.03 ],... 		'Style','text',... 		'String','Element',...		'Visible','on',... 		'tag','t_elem'); 	t_emin = uicontrol(... 		'BackgroundColor',bgcolor,... 		'ForegroundColor',txtcolor,... 		'Units','normalized',... 		'Position',[ 0.41 0.0 0.11 0.03 ],... 		'Style','text',... 		'String','Emin[eV]',...		'Visible','on',... 		'tag','t_emin'); 	t_emax = uicontrol(... 		'BackgroundColor',bgcolor,... 		'ForegroundColor',txtcolor,... 		'Units','normalized',... 		'Position',[ 0.53 0.0 0.11 0.03 ],... 		'Style','text',... 		'String','Emax[eV]',...		'Visible','on',... 		'tag','t_emax'); 	set(gcf,'WindowButtonMotionFcn','cursmode(''cromerg'')');%	set(gcf,'handlevisibility','callback');