function cromerg(command)% Gui version of cromerg% Copyright 2002 Sean Brennan.% Stanford Synchrotron Radiation Laboratory% Stanford Linear Accelerator Center, Stanford CA 94309% bren@slac.stanford.edu;global DISK_PATH DISK_END ANOMAL_FILES DISK_DELIMif nargin==0 % initialize window	crom_i;	cromerg('plot');	return;endif strcmp(command,'plot'),	e_name= get(findobj(gcf,'tag','crom_elem'),'string'); 	emin= str2num(get(findobj(gcf,'tag','crom_emin'),'string')); 	emax= str2num(get(findobj(gcf,'tag','crom_emax'),'string'));	zed= element(e_name);	if zed == 0, error('Not an element'),end	if emax > emin,		energy=[emin:10:emax]';		[fp,fpp]=anomal(zed,energy);		subplot(2,1,1), plot(energy,fp);		xlabel('Energy [eV]')		ylabel('Fp [electrons]')		subplot(2,1,2), plot(energy,fpp);		set(gca,'position',[0.13 0.15 0.775 0.3439])		xlabel('Energy [eV]')		ylabel('Fpp [electrons]')	endelseif strcmp(command,'curs'),	curs;end