function [enum,esymbol,ename]= element(name)% ELEMENT% [enum,esymbol,ename]= element(name) % Given a symbol name, returns an atomic number and the % element name in proper format. If invalid name, returns 0.% Modified 23-apr-95 SMB for either name or numberelement_symbols= ['H HeLiBeB C N O F NeNaMgAlSiP S ClArK CaScTiV ',...          'CrMnFeCoNiCuZnGaGeAsSeBrKrRbSrY ZrNbMoTcRuRhPdAg',...		  'CdInSnSbTeI XeCsBaLaCePrNdPmSmEuGdTbDyHoErTmYbLu',...		  'HfTaW ReOsIrPtAuHgTlPbBiPoAtRnFrRaAcThPaU '];element_names= ['Hydrogen    ';'Helium      ';'Lithium     ';...                'Beryllium   ';'Boron       ';'Carbon      ';...				'Nitrogen    ';'Oxygen      ';'Fluorine    ';...				'Neon        ';'Sodium      ';'Magnesium   ';...				'Aluminium   ';'Silicon     ';'Phosphorus  ';...				'Sulfur      ';'Chlorine    ';'Argon       ';...				'Potassium   ';'Calcium     ';'Scandium    ';...				'Titanium    ';'Vanadium    ';'Chromium    ';...				'Manganese   ';'Iron        ';'Cobalt      ';...				'Nickel      ';'Copper      ';'Zinc        ';...				'Gallium     ';'Germanium   ';'Arsenic     ';...				'Selenium    ';'Bromine     ';'Krypton     ';...				'Rubidium    ';'Strontium   ';'Yttrium     ';...				'Zirconium   ';'Niobium     ';'Molybdenum  ';...				'Technetium  ';'Ruthenium   ';'Rhodium     ';...				'Palladium   ';'Silver      ';'Cadmium     ';...				'Indium      ';'Tin         ';'Antimony    ';...				'Tellurium   ';'Iodine      ';'Xenon       ';...				'Cesium      ';'Barium      ';'Lanthanum   ';...				'Cerium      ';'Praseodymium';'Neodymium   ';...				'Promethium  ';'Samarium    ';'Europium    ';...				'Gadolinium  ';'Terbium     ';'Dysprosium  ';...				'Holmium     ';'Erbium      ';'Thulium     ';...				'Ytterbium   ';'Lutetium    ';'Hafnium     ';...				'Tantalum    ';'Tungsten    ';'Rhenium     ';...				'Osmium      ';'Iridium     ';'Platinum    ';...				'Gold        ';'Mercury     ';'Thallium    ';...				'Lead        ';'Bismuth     ';'Polonium    ';...				'Astatine    ';'Radon       ';'Francium    ';...				'Radium      ';'Actinium    ';'Thorium     ';...				'Protactinium';'Uranium     '];if nargin < 1	name=input('Enter element: ','s');endif isstr(name),	if all(abs(name)>47)&all(abs(name)<58),		elnum=str2num(name);	else		elnum= 0;	end	name=name(isletter(name));else	elnum=name;	if isempty(elnum), elnum=0; endendif elnum >0 & elnum <93	if nargout==0		disp(element_symbols(elnum*2-1:elnum*2));	else		enum=elnum;		esymbol= element_symbols(elnum*2-1:elnum*2);		if esymbol(2:2)==' ',		    clear esymbol;			esymbol= element_symbols(elnum*2-1:elnum*2-1);		end		ename=element_names(elnum,:);	end	returnendatomic_number=0;name= hilo(name);	% Make sure first letter is CAPS, second is lowername=abs(name);name=setstr(name);atomic_number=ceil(findstr(element_symbols,name)/2);if nargout==0	disp(sprintf(' %.0f\n',atomic_number))else	enum=atomic_number;	if enum>0,		esymbol= element_symbols(atomic_number*2-1:atomic_number*2);		if esymbol(2:2)==' ',		    clear esymbol;			esymbol= element_symbols(atomic_number*2-1:atomic_number*2-1);		end		ename=element_names(atomic_number,:);	else		ename='';		esymbol='';	endend