function name=hilo(name)% name=hilo(name)% convert element name so 1st char is capitalized,% second char is lower case% SMB 22-Feb-96 SSRL/SLAC Bren@slac.stanford.edulen= length(name);if len == 0    error('No element')elseif len == 1    name(2)= ' ';    if name(1) > 96, name(1)= name(1)- 32; end;elseif len == 2    if name(1) > 96, name(1)= name(1)- 32; end;    if name(2) < 96 & name(2)~= 32, name(2)= name(2)+ 32; end;end