function array=ptab(command)% Evaluates the input from the periodic table% Copyright 1996 Anneli Munkholm & Sean Brennan.% Stanford Synchrotron Radiation Laboratory% Stanford Linear Accelerator Center, Stanford CA 94309% bren@slac.stanford.edu; munkholm@anl.govif nargin==0	ptab_i;	return;end[elem_nr,symbol,elem_name]=element(command);[amu,rho,edges]=atomdata(elem_nr);disp(elem_name);disp(sprintf('Atomic number: %2.0f',elem_nr));disp(sprintf('Atomic weight: %3.2f',amu));disp(sprintf('Density      : %g g/cm^3',rho));edges= 1000*edges;leng=max(find(edges > 0.0));lengf=leng;if leng >4, leng= 4; endstr= sprintf('K-edge= %6.1f eV',edges(1));if leng > 1,    str=[str ', L-edges= '];    for i=2:leng,	    str=[str sprintf('%5.1f ',edges(i))];	endenddisp(str)if lengf > 3,    str= sprintf('Kalpha= %5.1f eV',edges(1)-edges(4));	if lengf > 6,	    str=[str sprintf(' Kbeta= %5.1f eV', edges(1)-edges(7))];    end	disp(str)end