function already= get_fig(my_fig_name);% get handles of all open figure windows% With MANY thanks to Dennis Brown, dwbrown@npg.navy.mil% 5-3-94 SMB (Bren@SLAC.stanford.edu), % changed to identify by name, 2-10-95 AMh = get(0,'Children');  %    (see ROOT in user manual)% handle_list = [];  % just in case no figures openalready=0;for i = 1:length(h),	fig_name = get(h(i),'name');	if strcmp(lower(my_fig_name),lower(fig_name)),			already= 1;           % does exist		figure(h(i));         % Make sure its current			break;	end;end;