function constrain=pk_gtcon(lam_len)% find constrainted variables for peakfit routine% 5-3-94 SMB (Bren@SLAC.stanford.edu)constrain=0.001*ones(lam_len,1);ck=flipud(sort(findobj(gcf,'Style','checkbox')));  % gets handles of checkbuttonsfor j=1:lam_len-2,	if get(ck(j),'Value') == 1,		constrain(j)= 0;	endend% check background and slope separatelyif get(findobj(gcf,'tag','ck17'),'Value') == 1,	constrain(lam_len-1)= 0;endif get(findobj(gcf,'tag','ck18'),'Value') == 1,	constrain(lam_len)= 0;end