function pk_tylam(FIT_LAMBDA)% function pk_tylam(FIT_LAMBDA)% put fitted values on plot for printing% 6-OCT-94 SMB[h1,h2]= pk_fndax;axes(h1);					% set the correct axis as currentset(gca,'visible','off');	% make sure its not visiblecla;						% get rid of any old valueslam_len= length(FIT_LAMBDA);field_xpos= [.35 .54 .72 0.89];field_ypos= [0.6 .42 .26 .08];npeaks= floor(lam_len/4);for i=1:npeaks	base= (i-1)*4;	for j=1:4		txt= num2str(FIT_LAMBDA(base+j));		text(field_xpos(j),field_ypos(i),txt);	endendtxt=  num2str(FIT_LAMBDA(lam_len-1));text(.17,.26,txt);txt=  num2str(FIT_LAMBDA(lam_len));text(.17,.08,txt);axes(h2);